<script setup>
import {ref, nextTick} from 'vue';

const stored = localStorage.getItem('form');

const storedPosY = localStorage.getItem('posY');

if (storedPosY)
    nextTick(() => {
        window.scrollTo(0, storedPosY);
    });

const form = ref(
    stored
        ? JSON.parse(stored)
        : {
              name: 'harry',
          },
);

const update = ({target}) => {
    form.value.name = target.value;
    localStorage.setItem('form', JSON.stringify(form.value));
};

let timeout;

document.addEventListener('scroll', () => {
    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(() => localStorage.setItem('posY', window.scrollY), 200);
});

